import React from 'react';
import {connect} from 'react-redux';
import moment from 'moment';


const TaskSummary = ({task}) => {
    return<div className="card blue-grey z-depth-0 task-summary">
        <div className="card-content white-text text-darken-3">
            <span className="card-title">{task.title}</span>
            <p>Posted by {task.authorFirstName + task.authorLastName}</p>
            <p className="grey-text">{moment(task.createdAt.toDate()).calendar()}</p>
        </div>
    </div>;
};

export default connect()(TaskSummary);
